mapper.update:
UPDATE restaurant_review_comments
SET
    like_count = (
        SELECT COUNT(*)
        FROM security.likes
        WHERE target_type = #{targetType}
        AND target_id = #{targetId}
        AND isLike = true
    ),
    dislike_count = (
        SELECT COUNT(*)
        FROM security.likes
        WHERE target_type = #{targetType}
        AND target_id = #{targetId}
        AND isLike = false
    )
WHERE id = #{targetId}

mapper.delete:
DELETE FROM security.likes
WHERE target_type = #{targetType}
AND target_id = #{targetId}
AND user_id = #{userId}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM security.likes
    WHERE target_type = #{targetType}
    AND target_id = #{targetId}
    AND user_id = #{userId}
)

mapper.update:
UPDATE security.likes
SET
    isLike = #{isLike},
    updatedAt = CURRENT_TIMESTAMP
WHERE target_type = #{targetType}
AND target_id = #{targetId}
AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM security.likes
WHERE target_type = #{targetType}
AND target_id = #{targetId}
AND isLike = #{isLike}

SELECT *
FROM security.likes
WHERE target_type = #{targetType}
AND target_id = #{targetId}
AND user_id = #{userId}
